package aqqje.com.autowrite;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;public class Main {    public static void main(String[] args) {        ApplicationContext context = new ClassPathXmlApplicationContext("beans-autowrite.xml");        Persion persion = (Persion) context.getBean("persion");        System.out.println(persion);    }}