package aqqje.com.beans.contollers;public class Persion {    private String name;    private String sex;    private int age;    private double monery;    public Persion(String name, String sex, int age) {        this.name = name;        this.sex = sex;        this.age = age;    }    public Persion(String name, String sex, double monery) {        this.name = name;        this.sex = sex;        this.monery = monery;    }    @Override    public String toString() {        return "Persion{" +                "name=" + name +                ", sex='" + sex + '\'' +                ", age=" + age +                ", monery=" + monery +                '}';    }    public String getName() {        return name;    }    public String getSex() {        return sex;    }    public void setSex(String sex) {        this.sex = sex;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public double getMonery() {        return monery;    }    public void setMonery(double monery) {        this.monery = monery;    }    public Persion() {    }    public void setName(String name) {        this.name = name;    }}