package aqqje.com.factory;import java.util.HashMap;import java.util.Map;/** * 实例工厂方法： */public class InstaceFactory {    private  Map<String, Object> cars;    public InstaceFactory(){        cars = new HashMap<>();        cars.put("audi", new Car("adui", 100000));        cars.put("ford", new Car("ford", 400000));    }    public Car getCar(String CarName){        return (Car)cars.get(CarName);    }}