package  aqqje.com.factory;import java.util.HashMap;import java.util.Map;/** * 静态工厂方法：直接调用某一个类的静态方法就可以返回一个 bean 实例 */public class StaticFactory {    private static Map<String, Object> cars = new HashMap<>();    static{        cars.put("audi", new Car("adui", 100000));        cars.put("ford", new Car("ford", 400000));    }    public static Car getCar(String carName){        return (Car)cars.get(carName);    }}