package aqqje.com.spel;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;public class Main {    public static void main(String[] args) {        ApplicationContext context = new ClassPathXmlApplicationContext("beans-spel.xml");        Car car = (Car)context.getBean("car");        System.out.println(car);        Adress adress = (Adress)context.getBean("adress");        System.out.println(adress);        Persion persion = (Persion)context.getBean("persion");        System.out.println(persion);    }}