/** * Copyright (c), 2018-2018, XXX有限公司 * FileName: Book * Author:   Administrator * Date:     2018/5/1022:37 * Description: * History: * <author>    <time>    <version>    <desc> * 作者姓名     修改时间      版本号       描述 */package aqqje.com.jdbc.txxml.beans;public class Book {    private Integer isbn;    private String bookname;    private double price;    public Book() {    }    public Book(Integer isbn, String bookname, double price) {        this.isbn = isbn;        this.bookname = bookname;        this.price = price;    }    public Integer getIsbn() {        return isbn;    }    public void setIsbn(Integer isbn) {        this.isbn = isbn;    }    public String getBookname() {        return bookname;    }    public void setBookname(String bookname) {        this.bookname = bookname;    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;    }    @Override    public String toString() {        return "Book{" +                "isbn=" + isbn +                ", bookname='" + bookname + '\'' +                ", price=" + price +                '}';    }}